import {useEffect, useState, useRef} from 'react';
import axios from 'axios';
import NavBar from '../Navbar';
import '../../css/memoryBook.css';
import CreatePost from './CreatePost';

import Button from '@material-ui/core/Button';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import IconButton from '@material-ui/core/IconButton';

export default function MemoryBook()
{
    const [userInfo,setUserInfo] = useState();
    const [partnerInfo,setPartnerInfo] = useState();
    const [isEditing,setIsEditing] = useState(false);
    const [isPosting,setIsPosting] = useState(false);
    const [posts,setPosts] = useState([]);

    const formRef = useRef();

    useEffect(()=>{
         axios.get('/auth/isLoggedIn')
        .then(res=>res.data)
        .catch(err=>console.log(err))
        .then(res=>{
            if(res.isLoggedIn)
            {
                setUserInfo(res.user);
                setPartnerInfo(res.partnerInfo);

                axios({
                    method:'post',
                    url:'/post/getPosts',
                })
                .then(res=>res.data)
                .catch(err=>console.log(err))
                .then(res=>{
                    if(res)
                    {
                        setPosts(res)
                    }
                })
            }
        })
    },[])  

    function handlePost()
    {
        setIsPosting(!isPosting);
        let state = !isPosting;
        
    }

    return <div className="memoryBook" id="memoryBook">
        {isPosting&&<CreatePost/>}
        <div className="">
            <NavBar/>
                <div className="postDiv">
                    <div className="post-option">
                    <h3>Post Some Memorable Moment Here</h3>
                    <IconButton id="post-btn" onClick={handlePost}><AddCircleOutlineIcon/></IconButton>
                    </div>
            </div>
        </div>
    </div>
}
