import { useContext, useEffect,useRef } from "react"
import { SocketContext } from "../SocketContext";
import '../css/videoCall.css';
import IconButton from '@material-ui/core/IconButton';
import VideocamOffIcon from '@material-ui/icons/VideocamOff';
import MicIcon from '@material-ui/icons/Mic';
import CallEndIcon from '@material-ui/icons/CallEnd';

export default function VideoCall()
{
    const {name,callAccepted,myVideo,userVideo,callEnded,stream,callUser,leaveCall,initializeVideo,muteAudio} = useContext(SocketContext);
        console.log(stream);
   
   let isVideo =true;
   function closeDisplay(){
        isVideo = !isVideo;
        stream.getVideoTracks()[0].enabled = isVideo;
   }
       
        return <div className="video-div" id="video-div">

                {stream&&
                <div className="vid-call">
                 <video playsInline muted ref={myVideo} autoPlay />        
                </div>}
                {callAccepted&&!callEnded&&
                <div className="vid-call1">
                <video playsInline muted ref={userVideo} autoPlay />
                </div>}
                <div className="option-div">
                <IconButton ><VideocamOffIcon/></IconButton>
                <IconButton onClick={leaveCall}><CallEndIcon/></IconButton>
                <IconButton onClick={muteAudio}><MicIcon/></IconButton>
                </div>
        </div>
}
