import '../../css/memoryPost.css'
import "react-image-gallery/styles/css/image-gallery.css";
import ReactAvatar from '@material-ui/core/Avatar';
import {Carousel} from 'react-responsive-carousel';
import ImageGallery from "react-image-gallery";


export default function MemoryPost(props)
{
    const{text,feeling,nickname,fileId,timeOfCreation,creatorId,userId} = props


    return<div className="post-div">
        <div className="post-header">
            {userId===creatorId?<ReactAvatar src="/gridFs/getProfile" id="post-avatar"/>:<ReactAvatar src="/gridFs/getPartnerProfile" id="post-avatar"/>}
           <div className="post-desc">
            <div className="post-feeling">
                <h3>{nickname}</h3>
                <h3> &nbsp; &bull; is feeling {feeling}</h3>  
            </div>
              <div className="post-time">
                <p>{timeOfCreation}</p>
              </div>
            </div> 
        </div>
        <div className="post-text">
        {text}
        </div>
        <div className="post-images">
            <ImageGallery items={constructGalleryUrls(urls)} showFullscreenButton={false} showPlayButton={false} showThumbnails={false}/>
        </div>
    </div>
}
