import {useEffect, useState, useRef} from 'react';
import axios from 'axios';
import ReactAvatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Activity from './ActivityPane';
import Navbar from '../components/Navbar';
import '../css/home.css';
import { IconButton } from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';


const useStyles = makeStyles((theme) => ({
  formControl: {
    minWidth: "100%",
  },
  large: {
    width: theme.spacing(17),
    height: theme.spacing(17),
  },
}));
export default function Home()
{
  const [userInfo,setUserInfo] = useState();
  const [havePartner,setHavePartner] = useState(false);
  const [roomId,setRoomId] = useState();
  const [reqId,setReqId] = useState();
  const [requestList,setRequestList]= useState([]);
  const [dayWidget,setDayWidget] = useState(false);
  const [specialWidget,setSpecialWidget] = useState(false);
  const [bdayWidget,setBdayWidget] = useState(false);


  const formRef = useRef();
  const acceptRef = useRef();

    useEffect(() => {
        const ac = new AbortController();
        axios.get('/auth/isLoggedIn')
        .then(res =>res.data)
        .catch(err => console.log(err))
        .then(res => {
          if (res.isLoggedIn===false)
          {
             window.open('/login','_self');
          }
          else
          {
            if(res.user.nickname===undefined)
            {
              window.open('/userProfileForm','_self');
            }
            else
            {

              if(res.widget)
              {
                if(res.widget.type)
                {

                }
              }

              axios({
                method:'get',
                url:'/user/getRequest',
                headers: {'Content-Type': 'multipart/form-data'}
              })
              .then(res=>res.data)
              .catch(err=>console.log(err))
              .then(res=>{
                let temp=[];
                res.requests.map(list=>{
                  temp.push({...list});
                })
                setRequestList(temp);
              })
              if(res.user.partner!==null)
              {
                setHavePartner(true); 
              }
              setUserInfo({...res.user});
            }
          } 
        })
        return function cancel() {
          ac.abort()
        }
      },[]);

      function handleLogout()
      {
        window.location.href = "/auth/logout";
      }
      function handleChatClick()
      {
        window.open('/chatRoom','_self');
      }

      function handleSendReq(event)
      {
        event.preventDefault();
        const formData = new FormData(formRef.current);
        axios({
          method:'post',
          url:'/user/sendRequest',
          data:formData,
          headers: {'Content-Type': 'multipart/form-data'}
        })
        .then(res=>res.data)
        .catch(err=>console.log(err))
        .then(res=>{
          if(res.isSent)
          {
             alert("sent!");
          }
          else
          {
            alert(res.status);
            console.log(res)
          }
        })
      }
      requestList.map(list=>{
        console.log(list);
      })

      function acceptPartner(event)
      {
        event.preventDefault();
        const formData = new FormData(acceptRef.current);
        axios({
          method:'post',
          url:'/user/acceptRequest',
          data:formData,
          headers: {'Content-Type': 'multipart/form-data'}
        })
        .then(res=>res.data)
        .catch(err=>console.log(err))
        .then(res=>{
          if(res.result)
          {
            console.log(res);
          }
        })
      }

      function onHoverAddWidget(type)
      {
        if(type==="special")
        {
          document.getElementById('hover').style.height = "100px";
          document.getElementById('hover').style.visibility = "visible";
        }
      }

      function offHoverAddWidget(type)
      {
        if(type==="special")
        {
          document.getElementById('hover').style.visibility = "hidden";
          document.getElementById('hover').style.height = "0px";        
        }
      }
    return <div className="home-div">
      <Navbar handleSendReq={handleSendReq} formRef = {formRef} handleLogout={handleLogout} handleSendReq={handleSendReq} acceptRef={acceptRef}/>
      <div className="home-background">
        <div className="home-overlay">
            <div className="home-widget anniverse">
            {!havePartner&&<div className="home-noPartner">You need to have a partner to access this widget</div>}
            {dayWidget?
            <div></div>
            :
            <div className="home-noWidget">
              <IconButton id="home-add-widget" onMouseOver={()=>onHoverAddWidget("special")} onMouseOut={()=>offHoverAddWidget("special")}><AddIcon fontSize="large"/></IconButton>
              <div className="home-add-hover" id="hover">Add this widget to get to know up coming special events</div>
            </div>}
            </div>
            <div className="home-widget days">
              
            </div>
            <div className="home-widget special">
              
            </div>

        </div>
            <div class="foreground"></div>
      </div>

    </div>
    
}
