import React, { createContext, useState, useRef, useEffect } from 'react';
import { io } from 'socket.io-client';
import Peer from 'simple-peer';
import sp from 'socket.io-stream';

const SocketContext = createContext();

const ENDPOINT='localhost:5000';
let socket;
const ContextProvider = ({ children }) => {
  const [callAccepted, setCallAccepted] = useState(false);
  const [callEnded, setCallEnded] = useState(false);
  const [stream, setStream] = useState();
  const [name, setName] = useState('');
  const [call, setCall] = useState({});
  const [userId,setUserId] = useState('');
  const [roomId,setRoomId] = useState('');
  const [newMessage,setNewMessage] = useState([]);
  const [partnerId,setPartnerId] = useState();
  
  const [isOnline,setIsOnline] = useState(true);
  const [users,setUsers] = useState();

  const myVideo = useRef();
  const userVideo = useRef();
  const connectionRef = useRef();
  const inititalRender = useRef(false);

   

  useEffect(() => {

    socket = io(ENDPOINT);

    socket.on('chatMsg',(chat)=>{
        setNewMessage(chat);
    })

    
  }, []);

  useEffect(()=>{
if(isCalling.current)
{
   initializeVideo();
    socket.on('me',(data)=>{
      console.log(data.id);
      console.log(data.users);
      console.log(data.users.length)
     
   })

   socket.on('returnDiv',e=>{
      document.getElementById("container").style.left = "50%"
      document.getElementById("container").style.width = "80%";
      document.getElementById("chat").style.width = "1000px";
      document.getElementById("video-div").style.display = "none";

   })

   socket.on('moveDiv',e=>{
      document.getElementById("container").style.left = "36%"
      document.getElementById("container").style.width = "70%";
      document.getElementById("chat").style.width = "800px";
      document.getElementById("video-div").style.display = "block";
      
   })
    socket.on('start',e=>{
      
      initializeVideo();
    }) 

    socket.on('calluser',async ({ from, name: callerName, signal })=> {
      setCall({ isReceivingCall: true, from, name: callerName, signal });
      
    });
  }
  },[])

  // useEffect(()=>{
  //   if(callAccepted)
  //   {
  //     navigator.mediaDevices.getUserMedia({ video: true, audio: true })
  //     .then((currentStream) => {
  //     setStream(currentStream);
  //     myVideo.current.srcObject = currentStream;
  //     });
  //   }
  // },[callAccepted])

  const startCall  =()=>{
    isCalling.current = true;
    socket.emit('calling');
  }
  
  const joinRoom = (roomId,userId,nickname) =>{
      socket.emit('join',{userId,roomId});
      setUserId(userId);
      setRoomId(roomId);
      setPartnerId(partnerId)
      setName(nickname);
  }

  const sendMsg = (msg,nickname) =>{
      socket.emit('sendMsg',{msg,userId,nickname,roomId})
  }

  const answerCall = async() => {
    setCallAccepted(true);
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.on('signal', (data) => {;
      socket.emit('answercall', { signal: data, to: call.from });
    });

    peer.on('stream', (currentStream) => {
      console.log(currentStream);
      userVideo.current.srcObject = currentStream;
    });
    peer.signal(call.signal);
    connectionRef.current = peer;
  };

  const initializeVideo= async()=>{
    await navigator.mediaDevices.getUserMedia({ video: true, audio: true })
    .then((currentStream) => {
    setStream(currentStream);
    console.log(currentStream);
    myVideo.current.srcObject = currentStream;
    }); 
    // socket.emit('streamFile',ss);
    // return ss;
    
  }

  const callUser = async() => {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on('signal', (data) => {
    socket.emit('calluser', {  signalData: data,  name });
      
    });

    peer.on('stream', (currentStream) => {
      userVideo.current.srcObject = currentStream;
    });

    socket.on('callaccepted', (signal) => {
      setCallAccepted(true);
      peer.signal(signal);
    });

    connectionRef.current = peer;
  };

  const leaveCall = () => {
    setCallEnded(true);
    document.getElementById("container").style.left = "50%"
    document.getElementById("container").style.width = "70%";
    document.getElementById("chat").style.width = "800px";
    document.getElementById("video-div").style.visibility = "none";
    connectionRef.current.destroy();

    window.location.reload();
  };

  return (
    <SocketContext.Provider value={{
      call,
      callAccepted,
      myVideo,
      newMessage,
      sendMsg,
      joinRoom,
      userVideo,
      stream,
      name,
      setName,
      callEnded,
      callUser,
      leaveCall,
      answerCall,
      users,
      isOnline,
      initializeVideo,
      startCall,
    }}
    >
      {children}
    </SocketContext.Provider>
  );
};

export { ContextProvider, SocketContext };
