import ReactPlayer from 'react-player'
import '../css/videoPlayer.css';
import IconButton from '@material-ui/core/IconButton';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import PauseIcon from '@material-ui/icons/Pause';
import MenuIcon from '@material-ui/icons/Menu';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';  
import VideoList from './VideoList';
import io from 'socket.io-client';
import TextField from '@material-ui/core/TextField';
import { useEffect, useRef, useState } from 'react';
import axios from 'axios';

let socket;
export default function Cinema()
{
  const ENDPOINT = 'localhost:5000';
  const [ready,setReady] = useState(false);
  const [uploadProgress,setUploadProgress] = useState(0);
  const [play,setPlay] =useState(false);
  const [isUpload,setIsUpload] = useState(false);
  const [buffering,setBuffering] = useState(true);
  const [url,setUrl] = useState();
  const videoRef = useRef();
  const formRef = useRef();
  const initialRun = useRef(true);
  useEffect(async()=>{
      const ac = new AbortController();
        socket = io(ENDPOINT);
        if(initialRun.current)
        {
            await axios.get('/user/getRoomId')
            .then(res=>res.data)
            .catch(err=>console.log(err))
            .then(res=>{
                socket.emit('join',res.roomInfo._id);
            });
            
            initialRun.current = false;
        }
        socket.on('changeState',state=>{
            setPlay(state);
        });

        socket.on('update',time=>{
            if(time>videoRef.current.getCurrentTime())
            {
                if(videoRef.current.getCurrentTime()<time-1){
                    videoRef.current.seekTo(String(time),'seconds');
                }
            }
        })

        socket.on('updateProgress',pointed=>{
            videoRef.current.seekTo(pointed,'seconds');
        })
      return function cancel(){
          ac.abort();
      }
  },[])

  function displayProgress()
  {
      socket.emit('keepUpdate',videoRef.current.getCurrentTime());
      const point = document.getElementById('progress-point');
      const value = videoRef.current.getCurrentTime()/(videoRef.current.getDuration()/100);
      point.style.left = value+"%";
  }
   
  function handleProgressChange(event)
  {
      const progressBarWidth = document.getElementById("progress-bar");
      const choose = event.nativeEvent.offsetX;
      const pointed = (choose/progressBarWidth.offsetWidth)*videoRef.current.getDuration();     
      socket.emit('changeProgress',pointed) 
      setPlay(true);
      
  }
  function handleVideoState()
  {
    socket.emit('changeVideoState',play);
  }
  async function handleUploadVideo(event)
  {
      event.preventDefault();
      const formdata = new FormData(formRef.current);
      await axios({
          method:'post',
          url:'/videoFs/upload',
          data:formdata,
          headers: {'Content-Type': 'multipart/form-data'},
          onUploadProgress:progressEvent=>{
              console.log(progressEvent.loaded);
            setUploadProgress(parseInt(Math.round((progressEvent.loaded*100)/progressEvent.total)));
            setTimeout(()=>setUploadProgress(0),100);
          },
      })
  }
  function submitUrl(event){
    event.preventDefault();
    const choosenUrl = document.getElementById('url').value;
    if(choosenUrl==="")
    {
        document.getElementById('url').value = "";
        return;
    }
    setReady(true);
    setUrl(choosenUrl);
    document.getElementById('url').value = "";
    console.log(choosenUrl)
  }
  function handleChooseMovie(filename)
  {
    const fileUrl = "/videoFs/"
  }
    return <div>
        <VideoList handleChooseMovie={handleChooseMovie}/>
        <form onSubmit={handleUploadVideo} ref={formRef}>
        <input type="file" name="video" id="video"/>
        <TextField
        id = "custom"
        className = "custom"
        name="custom"
        variant ="outlined"

        />
        <input type="submit"/>
        </form>
        <div>
            <form onSubmit={submitUrl}>
            <TextField
            id="url"
            label="TextField"
            />
            <Button
                type="submit"
                variant="contained"
                color="primary"
            > Send</Button>
            </form>
        </div>
        <div className="video-player">
        {buffering&&ready&&<div className="no-video">
            <h1>Loading....</h1>
        </div>}
        {ready?
        <ReactPlayer 
            ref={videoRef}
            playing={play}
            height="100%"
            width= "100%"
            url="/videoFs/getVideos"
            onProgress={displayProgress}
            onReady ={()=>setBuffering(false)}   
            onError={()=>setReady(false)}
        />:
        <div className="no-video">
            <h2>No Video</h2>
        </div>
        }
        {/* <video src="/videoFs/getVideos" type="video/mp4"/> */}
            <div style={{display:"flex"}}>
                <IconButton onClick={handleVideoState}>{play?<PauseIcon/>:<PlayArrowIcon/>}</IconButton>
                <div id="progress-bar" className="progress-bar status" onClick={handleProgressChange}>
                <div id="progress-point" className="progress-point"></div>
                </div>
                
            </div>
        </div>

    </div>
}
