import '../../css/memoryPost.css'
import ReactAvatar from '@material-ui/core/Avatar';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import IconButton from '@material-ui/core/IconButton';
import ImageGallery from "react-image-gallery";
import { useState } from 'react';


export default function MemoryPost(props)
{
    const{text,feeling,nickname,fileId,timeOfCreation,creatorId,userId,setIsEditing,isEditing} = props
    const [openMore,setOpenMore] = useState()

    function constructGalleryUrls(fileId)
    {
        return fileId.map(id=>{
            let url = "/post/getPostImage/"+id;
            return {original:url};
        })
    }
    return<div className="post-div">
        
        <IconButton id="post-more" onClick={()=>setIsEditing(!isEditing)}><MoreHorizIcon fontSize="medium"/></IconButton>
        <div className="post-header">
            {userId===creatorId?<ReactAvatar src="/gridFs/getProfile" id="post-avatar"/>:<ReactAvatar src="/gridFs/getPartnerProfile" id="post-avatar"/>}
           <div className="post-desc">
            <div className="post-feeling">
                <h3>{nickname}</h3>
                <h3> &nbsp;â€¢ &nbsp;is feeling {feeling}</h3>  
            </div>
              <div className="post-time">
                <p>{timeOfCreation}</p>
              </div>
            </div> 
        </div>
        <div className="post-text">
        {text}
        </div>
        <div className="post-images">
            <div className="post-img-div">
            <ImageGallery items={constructGalleryUrls(fileId)} showFullscreenButton={false} showPlayButton={false} showThumbnails={false} className="post-gallery" />
            </div>
        </div>
    </div>
}
