import {useEffect, useState,useRef} from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { set } from 'mongoose';
import MessageBox from './MessageBox';
let socket;
export default function ChatRoom(props)
{
    const ENDPOINT = 'localhost:5000';
    const [userInfo,setUserInfo] = useState();
    const [userId,setUserId] = useState();
    const [roomId,setRoomId] = useState();
    const [text,setText] = useState();
    const [userNickname,setUserNickname] = useState();

    useEffect(()=>{
        const ac = new AbortController();
        axios.get('/auth/isLoggedIn')
        .then(res =>res.data)
        .catch(err => console.log(err))
        .then(res => {
            if(res.isLoggedIn)
            {
              let userId = res.user._id;
                if(res.user.partner!==null)
                { 
                  axios({ 
                    method:'get',
                    url:'/user/getRoomId',
                    headers: {'Content-Type': 'multipart/form-data'}
                  })
                  .then(res=>res.data)
                  .catch(err=>console.log(err))
                  .then(res=>{
                    let roomId = res.roomInfo._id;
                    setRoomId(roomId);
                    socket = io(ENDPOINT);
                    socket.emit('join',{roomId,userId},({error})=>{

                    })
                  })
                }
                setUserId(res.user._id);
                setUserInfo({...res.user});
                setUserNickname(res.user.nickname);
            }
        });

        
        return function cancel() {
            ac.abort();
            socket.emit('disconnect');
            socket.off();
          }
    },[ENDPOINT])
    

    

    function handleSendMsg(event)
    {
      event.preventDefault();
      if(text.length>0)
      {
        console.log(userNickname);
        socket.emit('sendMsg',{text,roomId,userId,userNickname});
        setText("");
      }
    }
    return<div>
        <div>
            <h1>CHAT</h1>
        </div>
        <form onSubmit={handleSendMsg}>
        <input value={text} type="text" onChange={(event)=>setText(event.target.value)}/>
        <input type="submit"/>
        </form>
        <MessageBox/>
    </div>
}
