import {useEffect, useState, useRef} from 'react';
import axios from 'axios';
import NavBar from '../Navbar';
import '../../css/memoryBook.css';
import CreatePost from './CreatePost';

import Button from '@material-ui/core/Button';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import IconButton from '@material-ui/core/IconButton';

export default function MemoryBook()
{
    const [userInfo,setUserInfo] = useState();
    const [partnerInfo,setPartnerInfo] = useState();
    const [isEditing,setIsEditing] = useState(false);
    const [isPosting,setIsPosting] = useState(true);
    const [posts,setPosts] = useState([]);

    const formRef = useRef();

    useEffect(()=>{
         axios.get('/auth/isLoggedIn')
        .then(res=>res.data)
        .catch(err=>console.log(err))
        .then(res=>{
            if(res.isLoggedIn)
            {
                setUserInfo(res.user);
                setPartnerInfo(res.partnerInfo);

                axios({
                    method:'post',
                    url:'/post/getPosts',
                })
                .then(res=>res.data)
                .catch(err=>console.log(err))
                .then(res=>{
                    if(res)
                    {
                        setPosts(res)
                    }
                })
            }
        })
    },[])  

    function handlePost()
    {
        
        let state = !isPosting;
        if(state)
        {
            document.getElementById('memoryBook').style.opacity=0.3;
        }
        else
        {
            document.getElementById('memoryBook').style.opacity=1;
        }
        setIsPosting(!isPosting);
    }

    return <div className="memoryBook">
        {isPosting&&<CreatePost handlePost={handlePost} userInfo={userInfo}/>}
        <div className="memoryBook-div" id="memoryBook">
            <NavBar/>
                <div className="postDiv">
                    <div className="post-option">
                    <h3>Post Some Memorable Moment Here</h3>
                    <Button variant="contained" id="memory-create" onClick={handlePost}>
                    Create Memory
                    </Button>
                    </div>
            </div>
        </div>
    </div>
}
