import {useEffect, useState,useRef} from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { set } from 'mongoose';
import InputBox from './InputBox';
import '../css/chatroom.css';
import ChatNav from './ChatNav';

let socket;
export default function ChatRoom(props)
{
    const ENDPOINT = 'localhost:5000';
    const [userInfo,setUserInfo] = useState();
    const [userId,setUserId] = useState();
    const [roomId,setRoomId] = useState();
    const [text,setText] = useState("");
    const [userNickname,setUserNickname] = useState();
    const [messages,setMessages] = useState([]);
    const [partnerInfo,setPartnerInfo] = useState();

    useEffect(()=>{
        const ac = new AbortController();
        socket = io(ENDPOINT);
        axios.get('/auth/isLoggedIn')
        .then(res =>res.data)
        .catch(err => console.log(err))
        .then(res => {
            if(res.isLoggedIn)
            {
              let userId = res.user._id;
              setPartnerNickname(res.partnerNickname);
                if(res.user.partner!==null)
                { 
                  axios({ 
                    method:'get',
                    url:'/user/getRoomId',
                    headers: {'Content-Type': 'multipart/form-data'}
                  })
                  .then(res=>res.data)
                  .catch(err=>console.log(err))
                  .then(res=>{
                    let roomId = res.roomInfo._id;
                    setRoomId(roomId);
                    
                    socket.emit('join',{roomId,userId});
                  })
                }
                setUserId(res.user._id);
                setUserInfo({...res.user});
                setUserNickname(res.user.nickname);
            }
        });

        
        return function cancel() {
            ac.abort();
            socket.emit('disconnect');
            socket.off();
          }
    },[ENDPOINT])

    useEffect(()=>{
      const ac = new AbortController();
      socket.on('chatMsg',(chat)=>{
        setMessages(prevData=>{
          return [...prevData,chat];
        })
      })
      return function cancel() {
        ac.abort();
      }
    },[]);

    

    function handleSendMsg(event)
    {
      let msg = document.getElementById("chat").value;
      event.preventDefault();
      if(msg.length>0&&roomId)
      {
        socket.emit('sendMsg',{msg,roomId,userId,userNickname});
        setText("");
      }
      else{
        console.log("You cant send anything")
      }
      document.getElementById("chat").value = null;
    }

    return<div className="chat-background">
        <div>
          <ChatNav toggleBackHome={toggleBackHome} partnerNickname={partnerNickname} />
        </div>
        <form onSubmit={handleSendMsg}>
        <input type="text" id="chat"/>
        <input type="submit"/>
        </form>
        <InputBox />
    </div>
}
